svg.script:

	var tekoca_stran=1;
	var tekoci_primer=null;
	var st_strani=21;

	function on_load(evt) {
		// definira novi menu za ASV
		var newMenuRoot = parseXML( printNode( document.getElementById( 'NewMenu' ) ), contextMenu );
		contextMenu.replaceChild( newMenuRoot, contextMenu.firstChild );
		// pogleda, če je bil zahtevan direkten skok na podstran
//		sub_address = window.location.search.substring(1,window.location.search.length);
//		if (sub_address.length == 0)
//			return;
//		odpri_stran(sub_address);
	}

	function izpis(n,tekst) {
		var debug_txt = document.getElementById('debug_txt_polje_'+n);
		debug_txt.getFirstChild().setData(tekst);
	}

	function odpri_stran(n) {
		if (tekoca_stran!=n) {
			document.getElementById('stran_'+tekoca_stran).setAttribute("display","none");
			document.getElementById('stran_'+n).setAttribute("display","inline");
			tekoca_stran=n;
		}
	}

	function naprej() {
		if (tekoca_stran<st_strani) {
			document.getElementById('stran_'+tekoca_stran).setAttribute("display","none");
			document.getElementById('stran_'+(tekoca_stran+1)).setAttribute("display","inline");
			tekoca_stran++;
		}
	}

	function nazaj() {
		if (tekoca_stran>1) {
			document.getElementById('stran_'+tekoca_stran).setAttribute("display","none");
			document.getElementById('stran_'+(tekoca_stran-1)).setAttribute("display","inline");
			tekoca_stran--;
		}
	}

	function kazalo() {
		if (tekoca_stran!=0) {
			document.getElementById('stran_'+tekoca_stran).setAttribute("display","none");
			document.getElementById('stran_0').setAttribute("display","inline");
			tekoca_stran=0;
		}
	}

	function odpri_primer(niz) {
		document.getElementById('ikone_strani').setAttribute("display","none");
		document.getElementById('ikone_primeri').setAttribute("display","inline");
		document.getElementById('stran_'+tekoca_stran).setAttribute("display","none");
		document.getElementById(niz).setAttribute("display","inline");
		tekoci_primer=niz;
	}

	function zapri_primer() {
		document.getElementById('ikone_primeri').setAttribute("display","none");
		document.getElementById('ikone_strani').setAttribute("display","inline");
		document.getElementById(tekoci_primer).setAttribute("display","none");
		document.getElementById('stran_'+tekoca_stran).setAttribute("display","inline");
		tekoci_primer=null;
	}

	function uklopi_ozadje() {
		document.getElementById('iko_jazadaj').setAttribute("display","none");
		document.getElementById('ozadje').setAttribute("display","inline");
		document.getElementById('iko_nezadaj').setAttribute("display","inline");
	}

	function izklopi_ozadje() {
		document.getElementById('iko_nezadaj').setAttribute("display","none");
		document.getElementById('ozadje').setAttribute("display","none");
		document.getElementById('iko_jazadaj').setAttribute("display","inline");
	}

g.script:

			var log_counter=0;
			function log(text) {
				log_counter++;
				var event = document.getElementById('event_log');
				var new_event=event.cloneNode(1);
				new_event.getFirstChild().setData(log_counter+'.) '+text);
				new_event.setAttribute('id','event_log_'+log_counter);
				if (log_counter>33)
					event.parentNode.removeChild(document.getElementById('event_log_'+(log_counter-33)));
//					event.parentNode.setAttribute('y',event.parentNode.getAttribute('y')-event.getAttribute('dy'));
				event.parentNode.appendChild(new_event);
			}
